import "../interface/ICurveLikePool.sol";
import "../interface/IERC20.sol";
import "../interface/IWETH.sol";
import "../lib/SafeERC20.sol";
import "../YakAdapter.sol";

contract CurveLikeAdapter is YakAdapter {
    using SafeERC20 for IERC20;

    bytes32 public constant indentifier = keccak256("CurvelikeAdapter");
    mapping (address => bool) public isPoolToken;
    mapping (address => uint8) public tokenIndex;
    address public pool;

    constructor (
        string memory _name, 
        address _pool, 
        uint _swapGasEstimate
    ) {
        pool = _pool;
        name = _name;
        setSwapGasEstimate(_swapGasEstimate);
        _setPoolTokens();
    }

    // Mapping indicator which tokens are included in the pool 
    function _setPoolTokens() internal {
        for (uint8 i=0; true; i++) {
            try ICurveLikePool(pool).getToken(i) returns (address token) {
                isPoolToken[token] = true;
                tokenIndex[token] = i;
            } catch {
                break;
            }
        }
    }

    function setAllowances() public override onlyOwner {}

    function _approveIfNeeded(address _tokenIn, uint _amount) internal override {
        uint allowance = IERC20(_tokenIn).allowance(address(this), pool);
        if (allowance < _amount) {
            IERC20(_tokenIn).safeApprove(pool, UINT_MAX);
        }
    }

    function _query(
        uint _amountIn, 
        address _tokenIn, 
        address _tokenOut
    ) internal override view returns (uint) {
        if (
            !isPoolToken[_tokenIn] || 
            !isPoolToken[_tokenOut] ||
            _tokenIn == _tokenOut ||
            _amountIn == 0 ||
            ICurveLikePool(pool).paused()
        ) { return 0; }
        try ICurveLikePool(pool).calculateSwap(
            tokenIndex[_tokenIn], 
            tokenIndex[_tokenOut], 
            _amountIn
        ) returns (uint amountOut) {
            return amountOut;
        } catch {
            return 0;
        }
    }

    function _swap(
        uint _amountIn, 
        uint _amountOut, 
        address _tokenIn, 
        address _tokenOut, 
        address _to
    ) internal override {
        // Note that unsupported token will return index 0 which is valid
        ICurveLikePool(pool).swap(
            tokenIndex[_tokenIn], 
            tokenIndex[_tokenOut], 
            _amountIn, 
            _amountOut, 
            block.timestamp
        );
        // Confidently transfer amount-out
        _returnTo(_tokenOut, _amountOut, _to);
    }

}

